import{_ as a}from"./chunks/plugin-vue_export-helper.x3n3nnut.js";import{x as s,v as e,z as t}from"./chunks/vue.6z6dcVl3.js";const g=JSON.parse('{"title":"Windows 11 安装安卓子系统","description":"","frontmatter":{},"headers":[],"relativePath":"install-android.md","filePath":"install-android.md","lastUpdated":1698923772000}'),i={name:"install-android.md"},o=t('<h1 id="windows-11-安装安卓子系统" tabindex="-1">Windows 11 安装安卓子系统 <a class="header-anchor" href="#windows-11-安装安卓子系统" aria-label="Permalink to &quot;Windows 11 安装安卓子系统&quot;">​</a></h1><p>部分内容可参考：<a href="https://learn.microsoft.com/zh-cn/windows/android/wsa/" target="_blank" rel="noreferrer">适用于 Android™️ 的 Windows 子系统</a></p><h2 id="开启虚拟化平台" tabindex="-1">开启虚拟化平台 <a class="header-anchor" href="#开启虚拟化平台" aria-label="Permalink to &quot;开启虚拟化平台&quot;">​</a></h2><p>打开设置 → 应用 → 可选功能 → 更多 Windows 功能，在启动或关闭 Windows 功能中找到并勾选开启「Hyper-V」、「虚拟机平台」、「适用于 Linux 的 Windows 子系统」。</p><h2 id="安装安卓子系统" tabindex="-1">安装安卓子系统 <a class="header-anchor" href="#安装安卓子系统" aria-label="Permalink to &quot;安装安卓子系统&quot;">​</a></h2><p>打开 <a href="https://store.rg-adguard.net/" target="_blank" rel="noreferrer">https://store.rg-adguard.net/</a> 网站，在 URL（link）中粘贴以下地址</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>https://apps.microsoft.com/detail/9P3395VX91NR?hl=en-us&amp;gl=US</span></span></code></pre></div><p>粘贴后选择右侧下拉框的 Show，并点击右侧对钩。</p><p>然后下拉在下面的列表中选择 <code>MicrosoftCorporationII.WindowsSubsystemForAndroid_2309.40000.8.0_neutral_~_8wekyb3d8bbwe.msixbundle</code> 版本可能不一致，但最好选择最新的版本（最下面的版本）</p><p>以管理员方式运行 Windows PowerShell，使用 Add-AppxPackage 命令安装该包</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">Add-AppxPackage</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &quot;D:\\Lenovo\\SoftwarePackage\\MicrosoftCorporationII.WindowsSubsystemForAndroid_2309.40000.8.0_neutral_~_8wekyb3d8bbwe.Msixbundle&quot;</span></span></code></pre></div><p>安装完成之后，Windows 11 的开始菜单中便可以看到一个绿色的图标「适用于 Android™️ 的 Windows 子系统」</p><h2 id="连接-adb" tabindex="-1">连接 adb <a class="header-anchor" href="#连接-adb" aria-label="Permalink to &quot;连接 adb&quot;">​</a></h2><p>连接之前需要添加 <a href="https://developer.android.com/studio/releases/platform-tools?hl=zh-cn" target="_blank" rel="noreferrer">platform-tools</a> ，运行以下命令连接</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">adb</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> connect </span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">127.0</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">.0.1:58526</span></span></code></pre></div><p>如果提示 “由于目标计算机积极拒绝，无法连接。” 可以关闭后重启，或者点击一个应用开启一次 Android 子系统，再重启。</p><p>第一次连接会提示&quot;是否允许ADB调试？&quot;，一定要同意</p><p>之后就可以使用 <code>adb install</code> 安装安卓 apk 了</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">adb</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install &quot;D:\\Lenovo\\Downloads\\Coolapk-13.3.5-2310181-coolapk-app-sign.apk&quot;</span></span></code></pre></div>',19),n=[o];function d(l,p,r,h,c,k){return e(),s("div",null,n)}const _=a(i,[["render",d]]);export{g as __pageData,_ as default};
